import React, { useEffect, useState } from "react";
import '../index.css';
import TourDetails from './TourDetails.jsx';
import SERVER_URL from "./ServerUrl.js";

const TourCard = ({ tour, index }) => {
  const [user, setUser] = useState(null);
  const [isOrdered, setIsOrdered] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  // Отримати токен з локального сховища (Firebase auth зберігає токен тут, якщо ви вже автентифікувались)
  const getIdToken = async () => {
    const user = window?.firebase?.auth?.()?.currentUser;
    if (user) return await user.getIdToken();
    return null;
  };

  // Отримати поточного користувача з сервера
  const fetchCurrentUser = async () => {
    const token = await getIdToken();
    if (!token) return;

    try {
      const res = await fetch(`${SERVER_URL}/api/currentUser`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      if (!res.ok) throw new Error("Не авторизовано");
      const data = await res.json();
      setUser(data);

      // Тепер отримуємо список замовлених турів
      const userRes = await fetch(`${SERVER_URL}/api/user/${data.email}`);
      const userInfo = await userRes.json();

      if (userInfo.orderedTours?.includes(tour.id)) {
        setIsOrdered(true);
      }
    } catch (err) {
      console.error("Помилка автентифікації:", err);
    }
  };

  useEffect(() => {
    fetchCurrentUser();
  }, [tour.id]);

  const handleOrder = async () => {
    const token = await getIdToken();
    if (!token) {
      alert("Спочатку увійдіть у систему");
      return;
    }

    try {
      const res = await fetch(`${SERVER_URL}/api/order`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ tourId: tour.id })
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.error || "Помилка замовлення");

      setIsOrdered(true);
      closeDetails();
    } catch (error) {
      console.error("Помилка при замовленні:", error);
      alert("Виникла помилка при збереженні замовлення. Спробуйте ще раз.");
    }
  };

  const showTourDetails = () => {
    setShowDetails(true);
    const backgroundDiv = document.getElementById("tourDivBackground");
    backgroundDiv.style.display = "flex";
  };

  const closeDetails = () => {
    setShowDetails(false);
    const backgroundDiv = document.getElementById("tourDivBackground");
    backgroundDiv.style.display = "none";
  };

  return (
    <div className="tourDiv" id={tour.id}>
      <div className="tourName">
        <h1>{tour.title}</h1>
      </div>

      <div className="tourDescription">
        <div className="tourDescriptionImage">
          <img src={SERVER_URL + `/` + tour.img} alt={tour.title} className="tourImage" />
        </div>
        <div className="tourDescriptionText">
          <h2>{tour.description}</h2>
        </div>
      </div>

      <div id={`map-${index}`} className="map-container"></div>

      <div className="tourDivFooter">
        <div className="DurationDiv">
          <div className="DurationDivHead">
            <h1>Тривалість</h1>
          </div>
          <div className="DurationDivText">
            <h2>{tour.duration}</h2>
          </div>
        </div>
        <div className="PriceDiv">
          <div className="PriceDivHead">
            <h1>Ціна</h1>
          </div>
          <div className="PriceDivText">
            <h2>{tour.price} грн</h2>
          </div>
        </div>
        <div className="tourOrder">
          {!isOrdered && user && (
            <button className="tourOrderButton" onClick={showTourDetails}>
              <h2>Замовити</h2>
            </button>
          )}
        </div>
      </div>

      {showDetails && <TourDetails tour={tour} onClose={closeDetails} onOrder={handleOrder} />}
    </div>
  );
};

export default TourCard;
