import React, { useEffect, useState } from "react";
import '../index.css';
import TourDetails from './TourDetails.jsx';
import { getAuth } from "firebase/auth";

const TourCard = ({ tour, index }) => {
  const [user, setUser] = useState(null);
  const [isOrdered, setIsOrdered] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  const getCurrentUser = async () => {
    const auth = getAuth();
    const user = auth.currentUser;

    if (!user) return null;

    try {
      const idToken = await user.getIdToken();
      const response = await fetch("http://localhost:5000/api/currentUser", {
        headers: {
          Authorization: `Bearer ${idToken}`,
        },
      });

      if (!response.ok) throw new Error("Не вдалося отримати поточного користувача");

      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Помилка отримання поточного користувача:", error);
      return null;
    }
  };

  const checkIfTourOrdered = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/api/user/${email}`);
      if (!response.ok) throw new Error("Помилка отримання турів користувача");

      const data = await response.json();
      return data.orderedTours.includes(tour.id);
    } catch (error) {
      console.error("Помилка отримання турів користувача:", error);
      return false;
    }
  };

  const orderTour = async () => { 
  const auth = getAuth();
  const user = auth.currentUser;

  if (!user) {
    alert("Спочатку увійдіть у систему");
    return;
  }

  try {
    const idToken = await user.getIdToken();

    const response = await fetch("http://localhost:5000/api/user/order", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${idToken}`, // залиш, якщо плануєш перевірку на сервері
      },
      body: JSON.stringify({ email: user.email, tourId: tour.id }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`Не вдалося замовити тур. ${errorText}`);
    }

    setIsOrdered(true);
    console.log(`Тур з id=${tour.id} додано до замовлень`);
    closeDetails();
  } catch (error) {
    console.error("Помилка при додаванні замовлення:", error);
    alert("Виникла помилка при збереженні замовлення. Спробуйте ще раз.");
  }
};


  useEffect(() => {
    (async () => {
      const currentUser = await getCurrentUser();
      if (currentUser) {
        setUser(currentUser);
        const ordered = await checkIfTourOrdered(currentUser.email);
        setIsOrdered(ordered);
      }
    })();
  }, [tour.id]);

  const showTourDetails = () => {
    setShowDetails(true);
    const backgroundDiv = document.getElementById("tourDivBackground");
    backgroundDiv.style.display = "flex";
  };

  const closeDetails = () => {
    setShowDetails(false);
    const backgroundDiv = document.getElementById("tourDivBackground");
    backgroundDiv.style.display = "none";
  };

  return (
    <div className="tourDiv" id={tour.id}>
      <div className="tourName">
        <h1>{tour.title}</h1>
      </div>

      <div className="tourDescription">
        <div className="tourDescriptionImage">
          <img src={tour.img} alt={tour.title} className="tourImage" />
        </div>
        <div className="tourDescriptionText">
          <h2>{tour.description}</h2>
        </div>
      </div>

      <div id={`map-${index}`} className="map-container"></div>

      <div className="tourDivFooter">
        <div className="DurationDiv">
          <div className="DurationDivHead">
            <h1>Тривалість</h1>
          </div>
          <div className="DurationDivText">
            <h2>{tour.duration}</h2>
          </div>
        </div>
        <div className="PriceDiv">
          <div className="PriceDivHead">
            <h1>Ціна</h1>
          </div>
          <div className="PriceDivText">
            <h2>{tour.price} грн</h2>
          </div>
        </div>
        <div className="tourOrder">
          {!isOrdered && user && (
            <button className="tourOrderButton" onClick={showTourDetails}>
              <h2>Замовити</h2>
            </button>
          )}
        </div>
      </div>

      {showDetails && <TourDetails tour={tour} onClose={closeDetails} onOrder={orderTour} />}
    </div>
  );
};

export default TourCard;
