import React, { useEffect, useState } from "react";
import '../index.css';
import TourDetails from './TourDetails.jsx';
import SERVER_URL from "./ServerUrl.js";

import { getAuth } from "https://www.gstatic.com/firebasejs/10.8.0/firebase-auth.js"
import { initializeApp } from "https://www.gstatic.com/firebasejs/10.8.0/firebase-app.js"


const app = getApp();
const auth = getAuth(app);

const TourCard = ({ tour, index }) => {
  const [user, setUser] = useState(null);
  const [isOrdered, setIsOrdered] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  // --- Завантажити користувача через сервер ---
  useEffect(() => {
    const loadUser = async () => {
      const currentUser = auth.currentUser;
      if (currentUser) {
        const token = await currentUser.getIdToken();
        try {
          const response = await fetch(`${SERVER_URL}/api/currentUser`, {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });

          if (!response.ok) throw new Error('Не вдалося отримати користувача');

          const data = await response.json();
          setUser({ email: data.email });

          // Отримати інформацію про замовлені тури
          const userDataRes = await fetch(`${SERVER_URL}/api/user/${data.email}`);
          if (userDataRes.ok) {
            const userData = await userDataRes.json();
            setIsOrdered(userData.orderedTours.includes(tour.id));
          }
        } catch (error) {
          console.error('Помилка завантаження користувача через сервер:', error);
          setUser(null);
        }
      }
    };

    loadUser();
  }, [tour.id]);

  // --- Метод замовлення через сервер ---
  const handleOrder = async () => {
    if (!user) {
      alert("Спочатку увійдіть у систему");
      return;
    }

    try {
      const currentUser = auth.currentUser;
      const token = await currentUser.getIdToken();

      const response = await fetch(`${SERVER_URL}/api/order`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          tourId: tour.id
        })
      });

      if (!response.ok) throw new Error('Помилка при замовленні туру');

      setIsOrdered(true);
      console.log(`Тур з id=${tour.id} додано через сервер`);
      closeDetails();
    } catch (error) {
      console.error("Помилка при замовленні туру:", error);
      alert("Виникла помилка при збереженні замовлення через сервер.");
    }
  };

  const showTourDetails = () => {
    setShowDetails(true);
    document.getElementById("tourDivBackground").style.display = "flex";
  }

  const closeDetails = () => {
    setShowDetails(false);
    document.getElementById("tourDivBackground").style.display = "none";
  }

  return (
    <div className="tourDiv" id={tour.id}>
      <div className="tourName">
        <h1>{tour.title}</h1>
      </div>

      <div className="tourDescription">
        <div className="tourDescriptionImage">
          <img src={`${SERVER_URL}/${tour.img}`} alt={tour.title} className="tourImage" />
        </div>
        <div className="tourDescriptionText">
          <h2>{tour.description}</h2>
        </div>
      </div>

      <div id={`map-${index}`} className="map-container"></div>

      <div className="tourDivFooter">
        <div className="DurationDiv">
          <div className="DurationDivHead">
            <h1>Тривалість</h1>
          </div>
          <div className="DurationDivText">
            <h2>{tour.duration}</h2>
          </div>
        </div>
        <div className="PriceDiv">
          <div className="PriceDivHead">
            <h1>Ціна</h1>
          </div>
          <div className="PriceDivText">
            <h2>{tour.price} грн</h2>
          </div>
        </div>
        <div className="tourOrder">
          {!isOrdered && user && (
            <button className="tourOrderButton" onClick={showTourDetails}>
              <h2>Замовити</h2>
            </button>
          )}
        </div>
      </div>

      {showDetails && <TourDetails tour={tour} onClose={closeDetails} onOrder={handleOrder} />}
    </div>
  );
};

export default TourCard;
